    // Extract the content from the background SVG
    // Remove the XML declaration and outer svg tags
    $backgroundContent = preg_replace('/<\?xml.*?\?>/', '', $backgroundSVG);
    $backgroundContent = preg_replace('/<\/?svg.*?>/', '', $backgroundContent);

    // Escape text for XML
    $text = strtoupper(htmlspecialchars($text, ENT_QUOTES, 'UTF-8'));

    // Create SVG with sizing that will measure the text
    return <<<SVG
<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1000 562">
    <defs>
        <style>
            .text-overlay {
                font-family: {$opts['fontFamily']};
                /* Or use viewport units: */
                font-size: clamp(1rem, 11vw, 30vh);
                font-weight: {$opts['fontWeight']};
                fill: {$opts['color']};
                dominant-baseline: middle;
                text-anchor: middle;
            }
        </style>
    </defs>

    {$backgroundContent}
    <text
        class="text-overlay"
        x="50%"
        y="50%"
    >{$text}</text>
</svg>
SVG;
}

$svg = textToSVG(
    (new Apps\StdinReader())->read()
);

displayImageInTerminal($svg);
